import React, { useState } from 'react';
import { useBookings } from '../context/BookingContext';
import { useAuth } from '../context/AuthContext';
import { format } from 'date-fns';
import './PublicScheduleView.css';

const PublicScheduleView: React.FC = () => {
  const { getBookingsByDate } = useBookings();
  const { login } = useAuth();
  const [selectedDate, setSelectedDate] = useState(new Date().toISOString().split('T')[0]);
  
  // Login form state
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  // Generate time slots from 8 AM to 8 PM
  const timeSlots = Array.from({ length: 13 }, (_, i) => i + 8); // 8 AM to 8 PM

  const bookingsForDate = getBookingsByDate(selectedDate);

  const getBookingForTimeSlot = (hour: number) => {
    return bookingsForDate.find(booking => {
      const startHour = parseInt(booking.startTime.split(':')[0]);
      const endHour = parseInt(booking.endTime.split(':')[0]);
      return hour >= startHour && hour < endHour;
    });
  };

  const getBookingStyle = (booking: any, hour: number) => {
    const startHour = parseInt(booking.startTime.split(':')[0]);
    const endHour = parseInt(booking.endTime.split(':')[0]);
    const duration = endHour - startHour;
    
    return {
      height: `${duration * 60}px`,
      top: `${(hour - startHour) * 60}px`
    };
  };

  const getBookingTypeColor = (type: string) => {
    switch (type) {
      case 'meeting':
        return '#667eea';
      case 'teaching_session':
        return '#764ba2';
      case 'stand_up':
        return '#f093fb';
      default:
        return '#667eea';
    }
  };

  const handleLoginSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    
    if (login(username, password)) {
      // Login successful - will be handled by App component
    } else {
      setError('Invalid credentials. Use username: owner, password: password');
    }
  };

  return (
    <div className="public-schedule-container">
      <div className="public-header">
        <div className="header-content">
          <h1>Conference Room Booking System</h1>
          <p>View schedule and manage bookings</p>
        </div>
      </div>

      <div className="main-layout">
        {/* Left Side - Schedule */}
        <div className="schedule-section">
          <div className="schedule-header">
            <h2>Today's Schedule</h2>
            <div className="date-selector">
              <label htmlFor="date-select">Date:</label>
              <input
                type="date"
                id="date-select"
                value={selectedDate}
                onChange={(e) => setSelectedDate(e.target.value)}
                min={new Date().toISOString().split('T')[0]}
              />
            </div>
          </div>

          <div className="schedule-grid">
            <div className="time-column">
              {timeSlots.map(hour => (
                <div key={hour} className="time-slot">
                  {format(new Date().setHours(hour), 'h a')}
                </div>
              ))}
            </div>

            <div className="bookings-column">
              {timeSlots.map(hour => {
                const booking = getBookingForTimeSlot(hour);
                
                return (
                  <div key={hour} className="time-slot">
                    {booking && hour === parseInt(booking.startTime.split(':')[0]) && (
                      <div 
                        className="booking-item"
                        style={{
                          backgroundColor: getBookingTypeColor(booking.type),
                          ...getBookingStyle(booking, hour)
                        }}
                        title={`${booking.title} - ${booking.booker} (${booking.startTime} - ${booking.endTime})`}
                      >
                        <div className="booking-content">
                          <div className="booking-title">{booking.title}</div>
                          <div className="booking-details">
                            <span>{booking.booker}</span>
                            <span>{booking.startTime} - {booking.endTime}</span>
                          </div>
                          <div className="booking-type">
                            {booking.type.replace('_', ' ').toUpperCase()}
                          </div>
                        </div>
                      </div>
                    )}
                  </div>
                );
              })}
            </div>
          </div>

          <div className="schedule-legend">
            <h3>Legend</h3>
            <div className="legend-items">
              <div className="legend-item">
                <div className="legend-color" style={{ backgroundColor: '#667eea' }}></div>
                <span>Meeting</span>
              </div>
              <div className="legend-item">
                <div className="legend-color" style={{ backgroundColor: '#764ba2' }}></div>
                <span>Teaching Session</span>
              </div>
              <div className="legend-item">
                <div className="legend-color" style={{ backgroundColor: '#f093fb' }}></div>
                <span>Stand-up</span>
              </div>
            </div>
          </div>
        </div>

        {/* Right Side - Login */}
        <div className="login-section">
          <div className="login-card">
            <h3>Owner Login</h3>
            <p>Access booking management</p>
            
            <form onSubmit={handleLoginSubmit} className="login-form">
              <div className="form-group">
                <label htmlFor="username">Username:</label>
                <input
                  type="text"
                  id="username"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  required
                  placeholder="Enter username"
                />
              </div>
              
              <div className="form-group">
                <label htmlFor="password">Password:</label>
                <input
                  type="password"
                  id="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                  placeholder="Enter password"
                />
              </div>
              
              {error && <div className="error-message">{error}</div>}
              
              <button type="submit" className="login-button">
                Login
              </button>
            </form>
            
            <div className="login-info">
              <p><strong>Demo Credentials:</strong></p>
              <p>Username: owner</p>
              <p>Password: password</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PublicScheduleView;
