import React, { useState } from 'react';
import { AuthProvider, useAuth } from './context/AuthContext';
import { BookingProvider } from './context/BookingContext';
import Login from './components/Login';
import Navigation from './components/Navigation';
import ScheduleView from './components/ScheduleView';
import BookingForm from './components/BookingForm';
import PublicScheduleView from './components/PublicScheduleView';
import './App.css';

const AppContent: React.FC = () => {
  const { isAuthenticated } = useAuth();
  const [activeTab, setActiveTab] = useState<'schedule' | 'booking'>('schedule');
  const [showLogin, setShowLogin] = useState(false);

  // If user wants to login, show login page
  if (showLogin) {
    return <Login />;
  }

  // If not authenticated, show public schedule view
  if (!isAuthenticated) {
    return <PublicScheduleView onLoginClick={() => setShowLogin(true)} />;
  }

  // If authenticated, show owner dashboard
  return (
    <div className="app">
      <Navigation activeTab={activeTab} onTabChange={setActiveTab} />
      <main className="main-content">
        {activeTab === 'schedule' ? <ScheduleView /> : <BookingForm />}
      </main>
    </div>
  );
};

const App: React.FC = () => {
  return (
    <AuthProvider>
      <BookingProvider>
        <AppContent />
      </BookingProvider>
    </AuthProvider>
  );
};

export default App;
