import React, { useState } from 'react';
import { AuthProvider, useAuth } from './context/AuthContext';
import { BookingProvider } from './context/BookingContext';
import Navigation from './components/Navigation';
import ScheduleView from './components/ScheduleView';
import BookingForm from './components/BookingForm';
import PublicScheduleView from './components/PublicScheduleView';
import './App.css';

const AppContent: React.FC = () => {
  const { isAuthenticated } = useAuth();
  const [activeTab, setActiveTab] = useState<'schedule' | 'booking'>('schedule');

  // If authenticated, show owner dashboard
  if (isAuthenticated) {
    return (
      <div className="app">
        <Navigation activeTab={activeTab} onTabChange={setActiveTab} />
        <main className="main-content">
          {activeTab === 'schedule' ? <ScheduleView /> : <BookingForm />}
        </main>
      </div>
    );
  }

  // If not authenticated, show public schedule view with integrated login
  return <PublicScheduleView />;
};

const App: React.FC = () => {
  return (
    <AuthProvider>
      <BookingProvider>
        <AppContent />
      </BookingProvider>
    </AuthProvider>
  );
};

export default App;
