import React, { useState } from 'react';
import { useBookings } from '../context/BookingContext';
import { useNavigate } from 'react-router-dom';
import { format, addDays, startOfWeek, isSameDay, parseISO, isToday } from 'date-fns';
import './ScheduleView.css';
import './PublicScheduleView.css';

const HOURS = Array.from({ length: 13 }, (_, i) => i + 8); // 8 to 20
const DAYS = 7; // Sunday to Saturday

const MINUTES_IN_DAY = 60 * 24;
const DAY_START = 8 * 60; // 8:00
const DAY_END = 20 * 60; // 20:00

const PublicScheduleView: React.FC = () => {
  const { bookings } = useBookings();
  const [selectedDate, setSelectedDate] = useState(new Date().toISOString().split('T')[0]);
  const [modalBooking, setModalBooking] = useState<any>(null);
  const navigate = useNavigate();
  const selected = new Date(selectedDate);
  const weekStart = startOfWeek(selected, { weekStartsOn: 0 }); // Sunday
  const weekDates = Array.from({ length: DAYS }, (_, i) => addDays(weekStart, i));

  // Get all bookings for a given day
  const getBookingsForDay = (date: Date) => {
    return bookings.filter(booking => {
      const bookingDate = booking.date.length > 10 ? booking.date.split('T')[0] : booking.date;
      return isSameDay(parseISO(bookingDate), date);
    });
  };

  // Calculate top and height in percent for absolute positioning
  const getEventStyle = (booking: any) => {
    const [startHour, startMin] = booking.startTime.split(':').map(Number);
    const [endHour, endMin] = booking.endTime.split(':').map(Number);
    const start = startHour * 60 + startMin;
    const end = endHour * 60 + endMin;
    const top = ((start - DAY_START) / (DAY_END - DAY_START)) * 100;
    const height = ((end - start) / (DAY_END - DAY_START)) * 100;
    return {
      top: `${top}%`,
      height: `${height}%`,
      left: '8px',
      right: '8px',
      position: 'absolute' as 'absolute',
      zIndex: 2,
      cursor: 'pointer',
      borderRadius: '8px',
      boxShadow: '0 2px 8px rgba(0,0,0,0.13)',
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
      alignItems: 'flex-start',
      padding: '8px 12px',
      background: booking.type === 'meeting' ? '#888888' : booking.type === 'teaching_session' ? '#27ae60' : '#2980ef',
      color: '#fff',
    };
  };

  const handleEventClick = (booking: any) => {
    setModalBooking(booking);
  };

  return (
    <div className="public-schedule-container">
      <div className="public-header">
        <div className="header-content header-content-row">
          <h1 className="main-title">Conference Room Booking</h1>
          <button className="header-login-btn" onClick={() => navigate('/login')}>
            Login
          </button>
        </div>
      </div>
      <div className="week-schedule-header public-schedule-header-row">
        <div className="public-schedule-header-title"> </div>
        <div className="public-schedule-header-date-picker">
          <label htmlFor="week-date-picker" style={{ color: 'white', marginTop:"10px" }}>Select Week: </label>
          <input
            style={{ marginTop:"10px", marginRight:"10px" }}
            id="week-date-picker"
            type="date"
            value={selectedDate}
            onChange={e => setSelectedDate(e.target.value)}
          />
        </div>
      </div>
      <div className="gc-week-header-row">
        <div className="gc-week-header-left"></div>
        {weekDates.map((date, idx) => (
          <div key={date.toISOString()} className={`gc-week-header-cell${isToday(date) ? ' gc-today-header' : ''}`}>
            <div>{format(date, 'EEE')}</div>
            <div>{format(date, 'd')}</div>
          </div>
        ))}
      </div>
      <div className="gc-week-grid-row">
        <div className="gc-week-time-col">
          {HOURS.map((hour, idx) => (
            <div key={hour} className="gc-week-time-label">
              {format(new Date().setHours(hour, 0, 0, 0), 'h a')}
            </div>
          ))}
        </div>
        <div className="gc-week-grid">
          {weekDates.map((date, colIdx) => (
            <div key={date.toISOString()} className={`gc-week-day-col${isToday(date) ? ' gc-today-col' : ''}`}
              style={{ position: 'relative' }}>
              {/* Hour lines */}
              {HOURS.map((hour, rowIdx) => (
                <div key={hour} className="gc-week-hour-line"></div>
              ))}
              {/* Events */}
              {getBookingsForDay(date).map((booking: any) => (
                <div
                  key={booking.id}
                  className="gc-week-event"
                  style={getEventStyle(booking)}
                  onClick={() => handleEventClick(booking)}
                >
                  <div className="gc-week-event-title">{booking.title}</div>
                  <div className="gc-week-event-time">{booking.presenter} | {booking.startTime} - {booking.endTime}</div>
                  <div className="gc-week-event-type">{booking.type.replace('_', ' ').toUpperCase()}</div>
                </div>
              ))}
            </div>
          ))}
        </div>
      </div>
      {/* Modal for event details */}
      {modalBooking && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h3>{modalBooking.title}</h3>
            <div><b>Presenter:</b> {modalBooking.presenter}</div>
            <div><b>Time:</b> {modalBooking.startTime} - {modalBooking.endTime}</div>
            <div><b>Type:</b> {modalBooking.type.replace('_', ' ').toUpperCase()}</div>
            <div><b>Description:</b> {modalBooking.description}</div>
            <div style={{ display: 'flex', gap: 8, marginTop: 12 }}>
              <button onClick={() => setModalBooking(null)}>Close</button>
            </div>
          </div>
        </div>
      )}
      <div className="schedule-legend">
        <h3>Legend</h3>
        <div className="legend-items">
          <div className="legend-item">
            <div className="legend-color" style={{ backgroundColor: '#888888' }}></div>
            <span>Meeting</span>
          </div>
          <div className="legend-item">
            <div className="legend-color" style={{ backgroundColor: '#27ae60' }}></div>
            <span>Teaching Session</span>
          </div>
          <div className="legend-item">
            <div className="legend-color" style={{ backgroundColor: '#2980ef' }}></div>
            <span>Stand-up</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PublicScheduleView;
